modules.@gepheum/skir-fantasy-game-example/fanta:
// A fantasy RPG game state management system

import * as loot from "items/loot.skir";

/// Represents a point in 2D space
struct Point2D {
  x: float32 = 0;
  y: float32 = 1;
}

/// The primary attributes that define a character's capabilities
struct Attributes(891234) {
  /// Physical power affecting melee damage
  strength: int32 = 0;
  /// Agility affecting dodge chance and critical hits
  dexterity: int32 = 1;
  /// Mental fortitude affecting mana pool
  intelligence: int32 = 2;
  /// Life force determining hit points
  vitality: int32 = 3;
}

/// Character class determines available abilities and stat bonuses
enum CharacterClass(445231) {
  WARRIOR = 1;
  MAGE = 2;
  ROGUE = 3;
  CLERIC = 4;
}

/// Elemental damage types for spells and enchantments
enum Element(778899) {
  FIRE = 1;
  ICE = 2;
  LIGHTNING = 3;
  POISON = 4;
  HOLY = 5;
  SHADOW = 6;
}

/// A spell that can be cast by players or NPCs
struct Spell(334455) {
  spell_id: int32 = 0;
  /// Display name shown in the UI
  name: string = 1;
  /// Mana cost to cast this spell
  mana_cost: int32 = 2;
  /// Base damage before modifiers
  base_damage: float32 = 3;
  /// Elemental type for resistance calculations
  element: Element = 4;
  /// Cooldown in milliseconds
  cooldown_ms: int64 = 5;
  /// Area of effect radius, or null for single-target spells
  aoe_radius: float32? = 6;
}

/// Equipment slot where an item can be equipped
enum EquipmentSlot(556677) {
  HEAD = 1;
  CHEST = 2;
  LEGS = 3;
  FEET = 4;
  MAIN_HAND = 5;
  OFF_HAND = 6;
  RING = 7;
  AMULET = 8;
}

/// A piece of equipment that can be worn by a character
struct Equipment(223344) {
  item_id: hash64 = 0;
  name: string = 1;
  slot: EquipmentSlot = 2;
  /// Level requirement to equip this item
  required_level: int32 = 3;
  /// Bonus attributes granted when equipped
  stat_bonuses: Attributes = 4;

  struct ElementalResistance {
    element: Element = 0;
    resistance_value: float32 = 1;
  }
  /// Optional elemental resistance (0.0 = no resistance, 1.0 = immunity)
  elemental_resistance: ElementalResistance? = 5;

  /// Item rarity affects drop rates and vendor prices
  rarity: enum {
    COMMON = 1;
    UNCOMMON = 2;
    RARE = 3;
    EPIC = 4;
    LEGENDARY = 5;
  } = 6;
}

/// Status effects that can be applied to characters
enum StatusEffect(667788) {
  /// Character takes damage over time
  burning: struct {
    damage_per_second: float32 = 0;
    duration_ms: int64 = 1;
  } = 1;

  /// Character movement speed reduced
  frozen: struct {
    slow_percentage: float32 = 0;
    duration_ms: int64 = 1;
  } = 2;

  /// Character cannot take actions
  stunned: struct {
    duration_ms: int64;
  } = 3;

  /// Character is immune to damage
  invulnerable: struct {
    duration_ms: int64;
  } = 4;
}

/// Represents a player character or NPC in the game world
struct Character(112233) {
  character_id: hash64 = 0;
  name: string = 1;
  level: int32 = 2;
  class: CharacterClass = 3;

  /// Current and maximum health points
  health: struct {
    current: float32 = 0;
    maximum: float32 = 1;
  } = 4;

  /// Current and maximum mana points
  mana: struct {
    current: float32 = 0;
    maximum: float32 = 1;
  } = 5;

  /// Base attributes before equipment bonuses
  base_attributes: Attributes = 6;

  /// Current position in the world
  position: Point2D = 7;

  /// All learned spells, keyed by spell_id
  learned_spells: [Spell|spell_id] = 8;

  /// Currently equipped items
  equipment: [Equipment|slot.kind] = 9;

  /// Active status effects affecting this character
  active_effects: [StatusEffect] = 10;

  /// Experience points toward next level
  experience: int64 = 11;

  /// Timestamp of character creation
  created_at: timestamp = 12;

  /// Timestamp of last login (null for NPCs)
  last_login: timestamp? = 13;

  removed 14..16;
}

/// A quest that can be accepted and completed by players
struct Quest(998877) {
  quest_id: int32 = 0;
  title: string = 1;
  description: string = 2;
  /// Minimum level required to accept this quest
  required_level: int32 = 3;

  struct Objective {
    objective_id: int32 = 0;
    description: string = 1;
    /// Current progress (e.g., 5 out of 10 items collected)
    current_progress: int32 = 2;
    required_progress: int32 = 3;
  }
  /// Quest objectives that must be completed
  objectives: [Objective|objective_id] = 4;

  /// Quest completion status
  status: enum {
    AVAILABLE = 1;
    IN_PROGRESS = 2;
    COMPLETED = 3;
    FAILED = 4;
  } = 5;

  /// Rewards granted upon completion
  rewards: struct {
    experience: int64 = 0;
    gold: int32 = 1;
    items: [loot.Item] = 2;
  } = 6;
}

/// A guild that players can join for social and gameplay benefits
struct Guild(334422) {
  guild_id: hash64 = 0;
  name: string = 1;
  /// Guild leader's character ID
  leader_id: hash64 = 2;

  struct Member {
    character_id: hash64 = 0;
    rank: enum {
      MEMBER = 1;
      OFFICER = 2;
      LEADER = 3;
    } = 1;
    joined_at: timestamp = 2;
  }
  /// All guild members, keyed by character_id
  members: [Member|character_id] = 3;
  /// Guild level affects available perks
  level: int32 = 4;
  created_at: timestamp = 5;
}

/// Decision tree AI for NPC behavior
struct AIDecisionNode(445566) {
  question: string = 0;
  /// Condition that determines which branch to take
  yes: AIDecisionTree = 1;
  no: AIDecisionTree = 2;
}

/// Recursive AI behavior tree
enum AIDecisionTree(778800) {
  /// Leaf action: attack the player
  attack_action: string = 1;
  /// Leaf action: flee from danger
  flee_action: string = 2;
  /// Leaf action: use a healing potion
  heal_action: string = 3;
  /// Branch node with conditional logic
  decision_node: AIDecisionNode = 4;
}

/// Combat action performed by a character
enum CombatAction(889900) {
  /// Basic melee attack
  MELEE_ATTACK = 1;
  /// Cast a spell targeting one or more characters
  spell_cast: struct {
    spell_id: int32 = 0;
    /// IDs of affected targets
    target_ids: [hash64] = 1;
  } = 2;
  /// Use a consumable item
  item_use: struct {
    item_id: hash64;
  } = 3;
  /// Defend to reduce incoming damage
  DEFEND = 4;
  /// Attempt to flee from combat
  FLEE = 5;
}

// === RPC Methods ===

/// Request to create a new character
method CreateCharacter(
  struct {
    name: string = 0;
    class: CharacterClass = 1;
  }
): struct {
  /// The newly created character, or null if name was taken
  character: Character? = 0;
  /// Error message if character creation failed
  error: string? = 1;
} = 100001;

/// Request to execute a combat action
method PerformCombatAction(
  struct {
    character_id: hash64 = 0;
    action: CombatAction = 1;
  }
): struct {
  /// Whether the action succeeded
  success: bool = 0;
  /// Detailed combat log entries
  combat_log: [string] = 1;
  /// Updated character state after the action
  updated_character: Character = 2;
} = 100002;

/// Request to join a guild
method JoinGuild(
  struct {
    character_id: hash64 = 0;
    guild_id: hash64 = 1;
  }
): struct {
  success: bool = 0;
  error_message: string? = 1;
} = 100003;

/// Get the leaderboard of top players
method GetLeaderboard(
  struct {
    /// Filter by character class, or null for all classes
    class_filter: CharacterClass? = 0;
    /// Maximum number of entries to return
    limit: int32 = 1;
  }
): struct {
  struct Entry {
    rank: int32 = 0;
    character_name: string = 1;
    level: int32 = 2;
    class: CharacterClass = 3;
  }
  entries: [Entry] = 0;
} = 100004;

// === Constants ===

/// Default starting attributes for new characters
const DEFAULT_STARTING_ATTRIBUTES: Attributes = {
  strength: 10,
  dexterity: 10,
  intelligence: 10,
  vitality: 10,
};

/// A legendary sword with exceptional stats
const EXCALIBUR: Equipment = {
  item_id: 999999,
  name: "Excalibur, Blade of Kings",
  slot: "MAIN_HAND",
  required_level: 50,
  stat_bonuses: {
    strength: 100,
    dexterity: 50,
    intelligence: 25,
    vitality: 75,
  },
  elemental_resistance: {
    element: "HOLY",
    resistance_value: 0.8,
  },
  rarity: "LEGENDARY",
};

/// Basic fireball spell available to all mages
const FIREBALL: Spell = {
  spell_id: 1001,
  name: "Fireball",
  mana_cost: 30,
  base_damage: 150.0,
  element: "FIRE",
  cooldown_ms: 5000,
  aoe_radius: 10.0,
};

/// Starter quest for new players
const TUTORIAL_QUEST: Quest = {
  quest_id: 1,
  title: "Welcome to the Realm",
  description: "Learn the basics of combat by defeating 5 slimes.",
  required_level: 1,
  objectives: [
    {
      objective_id: 1,
      description: "Defeat slimes",
      current_progress: 0,
      required_progress: 5,
    },
  ],
  status: "AVAILABLE",
  rewards: {
    experience: 100,
    gold: 50,
    items: [],
  },
};

modules.@gepheum/skir-fantasy-game-example/items:
// Loot and item management for the fantasy RPG

/// Item quality affects appearance and vendor value multiplier
enum ItemQuality(223355) {
  POOR = 1;
  COMMON = 2;
  UNCOMMON = 3;
  RARE = 4;
  EPIC = 5;
  LEGENDARY = 6;
  MYTHIC = 7;
}

/// Types of consumable items
enum ConsumableType(334466) {
  /// Restores health points
  HEALTH_POTION = 1;
  /// Restores mana points
  MANA_POTION = 2;
  /// Grants temporary buff
  BUFF_ELIXIR = 3;
  /// Removes debuffs
  CURE_POTION = 4;
  /// Grants experience boost
  XP_SCROLL = 5;
}

/// A consumable item that can be used by players
struct Consumable(445577) {
  consumable_type: ConsumableType = 0;
  /// Amount to restore (health/mana) or duration in ms (buffs)
  potency: float32 = 1;
  /// For buff elixirs, description of the effect
  effect_description: string? = 2;
}

/// Categories of crafting materials
enum MaterialCategory(556688) {
  ORE = 1;
  HERB = 2;
  LEATHER = 3;
  CLOTH = 4;
  ESSENCE = 5;
  GEM = 6;
}

/// Raw materials used in crafting
struct CraftingMaterial(667799) {
  material_id: int32 = 0;
  name: string = 1;
  category: MaterialCategory = 2;
  /// Tier affects which recipes can use this material (1 = basic, 5 = master)
  tier: int32 = 3;
  /// Stack size limit in inventory
  max_stack_size: int32 = 4;
}

/// A generic item that can be found as loot
struct Item(778811) {
  item_id: hash64 = 0;
  name: string = 1;
  description: string = 2;
  quality: ItemQuality = 3;
  /// Base vendor sell price in gold
  base_value: int32 = 4;
  /// Item level affects stat scaling
  item_level: int32 = 5;
  /// Weight affects encumbrance
  weight: float32 = 6;

  /// The actual item type and properties
  item_type: enum {
    /// Equipment piece (weapon, armor, accessories)
    equipment: struct {
      /// Equipment data would be imported from the main schema
      /// For simplicity, just storing the equipment ID here
      equipment_id: hash64;
    } = 1;

    /// Consumable item
    consumable: Consumable = 2;

    /// Crafting material
    material: CraftingMaterial = 3;

    /// Quest item (cannot be sold or traded)
    quest_item: struct {
      quest_id: int32 = 0;
      /// Flavor text for the quest item
      lore: string = 1;
    } = 4;

    /// Junk item (only purpose is to sell to vendors)
    JUNK = 5;
  } = 7;

  /// Whether this item can be traded between players
  is_tradeable: bool = 8;

  /// Whether this item binds to the character when picked up
  binds_on_pickup: bool = 9;
}

/// Loot drop configuration for monsters or treasure chests
struct LootTable(889922) {
  loot_table_id: int32 = 0;
  /// Descriptive name for this loot table
  name: string = 1;

  struct Drop {
    item_id: hash64 = 0;
    /// Probability from 0.0 to 1.0
    drop_chance: float32 = 1;
    /// Minimum quantity that can drop
    min_quantity: int32 = 2;
    /// Maximum quantity that can drop
    max_quantity: int32 = 3;
  }
  /// Possible item drops with their drop rates
  drops: [Drop] = 2;

  struct GoldDrop {
    min_gold: int32 = 0;
    max_gold: int32 = 1;
  }
  /// Guaranteed gold drop range
  gold_drop: GoldDrop? = 3;
}

/// Player inventory management
struct Inventory(990033) {
  /// Maximum number of item stacks allowed
  capacity: int32 = 0;

  struct Item {
    item: Item = 0;
    /// How many of this item (for stackable items)
    quantity: int32 = 1;
    /// Inventory slot position
    slot: int32 = 2;
  }
  /// Items currently in inventory
  items: [Item|slot] = 1;

  /// Current total weight of all items
  total_weight: float32 = 2;

  /// Maximum weight before becoming encumbered
  max_weight: float32 = 3;
}

// === Constants ===

/// A basic health potion
const MINOR_HEALTH_POTION: Item = {
  item_id: 1001,
  name: "Minor Health Potion",
  description: "Restores 50 health points.",
  quality: "COMMON",
  base_value: 5,
  item_level: 1,
  weight: 0.1,
  item_type: {
    kind: "consumable",
    value: {
      consumable_type: "HEALTH_POTION",
      potency: 50.0,
      effect_description: null,
    },
  },
  is_tradeable: true,
  binds_on_pickup: false,
};

/// Iron ore for crafting
const IRON_ORE: Item = {
  item_id: 2001,
  name: "Iron Ore",
  description: "A chunk of raw iron ore. Can be smelted into iron bars.",
  quality: "COMMON",
  base_value: 2,
  item_level: 1,
  weight: 0.5,
  item_type: {
    kind: "material",
    value: {
      material_id: 1,
      name: "Iron Ore",
      category: "ORE",
      tier: 1,
      max_stack_size: 100,
    },
  },
  is_tradeable: true,
  binds_on_pickup: false,
};

/// Example loot table for a basic goblin enemy
const GOBLIN_LOOT: LootTable = {
  loot_table_id: 1,
  name: "Goblin Loot",
  drops: [
    {
      item_id: 1001,
      drop_chance: 0.3,
      min_quantity: 1,
      max_quantity: 1,
    },
    {
      item_id: 2001,
      drop_chance: 0.15,
      min_quantity: 1,
      max_quantity: 3,
    },
  ],
  gold_drop: {
    min_gold: 1,
    max_gold: 5,
  },
};

modules.@gepheum/skir-golden-tests/goldens.skir:
struct UnitTest {
  test_number: int32;
  assertion: Assertion;
}

enum Assertion {
  struct BytesEqual {
    actual: BytesExpression;
    expected: BytesExpression;
  }
  bytes_equal: BytesEqual;

  struct BytesIn {
    actual: BytesExpression;
    // The assertion is satisfied if this array contains the actual value.
    expected: [bytes];
  }
  bytes_in: BytesIn;

  struct StringEqual {
    actual: StringExpression;
    expected: StringExpression;
  }
  string_equal: StringEqual;

  struct StringIn {
    actual: StringExpression;
    // The assertion is satisfied if this array contains the actual value.
    expected: [string];
  }
  string_in: StringIn;

  struct ReserializeValue {
    value: TypedValue;
    expected_dense_json: [string];
    expected_readable_json: [string];
    alternative_jsons: [StringExpression];
    expected_bytes: [bytes];
    alternative_bytes: [BytesExpression];
    expected_type_descriptor: string?;
  }
  reserialize_value: ReserializeValue;

  struct ReserializeLargeString {
    num_chars: int32;
    expected_byte_prefix: bytes;
  }
  reserialize_large_string: ReserializeLargeString;

  struct ReserializeLargeArray {
    num_items: int32;
    expected_byte_prefix: bytes;
  }
  reserialize_large_array: ReserializeLargeArray;
}

enum StringExpression {
  literal: string;
  to_dense_json: TypedValue;
  to_readable_json: TypedValue;
}

enum BytesExpression {
  literal: bytes;
  to_bytes: TypedValue;
}

enum TypedValue {
  bool: bool;
  int32: int32;
  int64: int64;
  hash64: hash64;
  float32: float32;
  float64: float64;
  timestamp: timestamp;
  string: string;
  bytes: bytes;
  bool_optional: bool?;
  ints: [int32];
  point: Point;
  color: Color;
  my_enum: MyEnum;
  keyed_arrays: KeyedArrays;
  rec_struct: RecStruct;
  rec_enum: RecEnum;
  round_trip_dense_json: TypedValue;
  round_trip_readable_json: TypedValue;
  round_trip_bytes: TypedValue;
  point_from_json_keep_unrecognized: StringExpression;
  point_from_json_drop_unrecognized: StringExpression;
  point_from_bytes_keep_unrecognized: BytesExpression;
  point_from_bytes_drop_unrecognized: BytesExpression;
  color_from_json_keep_unrecognized: StringExpression;
  color_from_json_drop_unrecognized: StringExpression;
  color_from_bytes_keep_unrecognized: BytesExpression;
  color_from_bytes_drop_unrecognized: BytesExpression;
  my_enum_from_json_keep_unrecognized: StringExpression;
  my_enum_from_json_drop_unrecognized: StringExpression;
  my_enum_from_bytes_keep_unrecognized: BytesExpression;
  my_enum_from_bytes_drop_unrecognized: BytesExpression;
}

/// A point
struct Point {
  removed;
  /// x coordinate
  x: int32;
  removed;
  removed;
  y: int32;
  removed;
  color: Color;
}

struct Color {
  r: int32 = 0;
  g: int32 = 1;
  b: int32 = 2;
  removed 3;
}

/// my enum
enum MyEnum {
  /// all good
  OK = 1;
  color: Color = 4;
  bool: bool = 5;
  removed 2;
}

struct KeyedArrays {
  enum Weekday {
    MONDAY;
  }
  struct SubItem {
    weekday: Weekday;
  }
  struct Item {
    sub_item: SubItem;
  }
  items: [Item|sub_item.weekday.kind];
}

struct RecStruct {
  a: RecStruct;
  b: bool;
}

enum RecEnum {
  FOO;
  e: RecEnum;
}

const UNIT_TESTS: [UnitTest] = [
  {
    test_number: 1000,
    assertion: {
      kind: "reserialize_value",
      value: {|
        value: {
          kind: "bool",
          value: true,
        },
        expected_dense_json: [
          "1",
        ],
        expected_readable_json: [
          "true",
        ],
        alternative_jsons: [
          {
            kind: "literal",
            value: "2.5",
          },
          {
            kind: "literal",
            value: "-1",
          },
          {
            kind: "literal",
            value: '"1"',
          },
        ],
        expected_bytes: [
          "hex:736b697201",
        ],
        alternative_bytes: [
          {
            kind: "to_bytes",
            value: {
              kind: "float32",
              value: 2.5,
            },
          },
        ],
        expected_type_descriptor: "{\n  \"type\": {\n    \"kind\": \"primitive\",\n    \"value\": \"bool\"\n  },\n  \"records\": []\n}",
      |},
    },
  },
  {
    test_number: 1001,
    assertion: {
      kind: "reserialize_value",
      value: {|
        value: {
          kind: "bool",
          value: false,
        },
        expected_dense_json: [
          "0",
        ],
        expected_readable_json: [
          "false",
        ],
        expected_bytes: [
          "hex:736b697200",
        ],
        alternative_jsons: [
          {
            kind: "literal",
            value: "0.0",
          },
          {
            kind: "literal",
            value: "-0.0",
          },
          {
            kind: "literal",
            value: "\"0\"",
          },
        ],
      |},
    },
  },
  {
    test_number: 1002,
    assertion: {
      kind: "reserialize_value",
      value: {|
        value: {
          kind: "timestamp",
          value: "1970-01-01T00:00:00.000Z",
        },
        expected_dense_json: [
          "0",
        ],
        expected_readable_json: [
          '{\n  "unix_millis": 0,\n  "formatted": "1970-01-01T00:00:00.000Z"\n}',
          '{\n  "unix_millis": 0,\n  "formatted": "1970-01-01T00:00:00Z"\n}',
        ],
        expected_bytes: [
          "hex:736b697200",
        ],
        alternative_jsons: [
          {
            kind: "literal",
            value: "0.0",
          },
        ],
        expected_type_descriptor: "{\n  \"type\": {\n    \"kind\": \"primitive\",\n    \"value\": \"timestamp\"\n  },\n  \"records\": []\n}",
      |},
    },
  },
  {
    test_number: 1003,
    assertion: {
      kind: "reserialize_value",
      value: {|
        value: {
          kind: "timestamp",
          value: "2023-08-25T21:30:34.586Z",
        },
        expected_dense_json: [
          "1692999034586",
        ],
        expected_readable_json: [
          '{\n  "unix_millis": 1692999034586,\n  "formatted": "2023-08-25T21:30:34.586Z"\n}',
        ],
        expected_bytes: [
          "hex:736b6972efda269b2e8a010000",
        ],
        alternative_jsons: [
          {
            kind: "literal",
            value: "\"1692999034586\"",
          },
        ],
        alternative_bytes: [
          {
            kind: "to_bytes",
            value: {
              kind: "float64",
              value: 1692999034586.1,
            },
          },
        ],
      |},
    },
  },
  {
    test_number: 1004,
    assertion: {
      kind: "reserialize_value",
      value: {|
        value: {
          kind: "int32",
          value: 2,
        },
        expected_dense_json: [
          "2",
        ],
        expected_readable_json: [
          "2",
        ],
        expected_bytes: [
          "hex:736b697202",
        ],
        alternative_jsons: [
          {
            kind: "literal",
            value: "\"2\"",
          },
          {
            kind: "literal",
            value: "2.0",
          },
        ],
        alternative_bytes: [
          {
            kind: "to_bytes",
            value: {
              kind: "float64",
              value: 2.0,
            },
          },
        ],
        expected_type_descriptor: "{\n  \"type\": {\n    \"kind\": \"primitive\",\n    \"value\": \"int32\"\n  },\n  \"records\": []\n}",
      |},
    },
  },
  {
    test_number: 1005,
    assertion: {
      kind: "reserialize_value",
      value: {|
        value: {
          kind: "int32",
          value: -1,
        },
        expected_dense_json: [
          "-1",
        ],
        expected_readable_json: [
          "-1",
        ],
        expected_bytes: [
          "hex:736b6972ebff",
        ],
        alternative_jsons: [
          {
            kind: "literal",
            value: "\"-1\"",
          },
        ],
      |},
    },
  },
  {
    test_number: 1006,
    assertion: {
      kind: "reserialize_value",
      value: {|
        value: {
          kind: "int32",
          value: 231,
        },
        expected_dense_json: [
          "231",
        ],
        expected_readable_json: [
          "231",
